import React from "react";
import { Link } from "react-router-dom";
import { ArrowRight, Package, Edit, Trash2 } from "lucide-react";
import Button from "../components/ui/Button";

const FeatureCard = ({ icon, title, description }) => (
  <div className="bg-white p-6 rounded-xl shadow-md border border-gray-100 hover:shadow-xl transform hover:-translate-y-1 transition-all duration-500">
    <div className="flex items-center justify-center w-14 h-14 bg-blue-100 rounded-full mb-4 text-2xl">
      {icon}
    </div>
    <h3 className="text-lg font-semibold mb-2">{title}</h3>
    <p className="text-gray-600 text-sm">{description}</p>
  </div>
);

export default function Home() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 flex flex-col justify-center items-center space-y-16">
      
      {/* Emoji Spin Animation */}
      <div className="text-8xl animate-spin-slow mb-6">ðŸ›’</div>

      {/* Hero Section */}
      <div className="text-center max-w-3xl">
        <h1 className="text-4xl md:text-6xl font-extrabold text-gray-900 leading-tight mb-4 fade-in">
          Streamline Your <span className="text-blue-600">Product Workflow</span>
        </h1>
        <p className="text-lg text-gray-600 mb-8 fade-in-delay">
          A simple, elegant, and powerful solution to manage your product inventory.
          Add, edit, sort, and search with ease.
        </p>

        <Link to="/products" className="fade-in-delay-2">
          <Button size="lg" className="bg-blue-600 hover:bg-blue-700 transition-all duration-300">
            Go to Products
            <ArrowRight className="w-5 h-5 ml-2" />
          </Button>
        </Link>
      </div>

      {/* Features Section */}
      <div className="py-20 bg-gray-50 w-full">
        <div className="max-w-7xl mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-2">Core Features</h2>
            <p className="text-md text-gray-600">Everything you need in a modern product manager.</p>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <FeatureCard
              icon={<Package className="text-blue-600 w-6 h-6" />}
              title="Add & View Products"
              description="Easily add new products to your inventory and view them in a clean, organized grid."
            />
            <FeatureCard
              icon={<Edit className="text-blue-600 w-6 h-6" />}
              title="Edit & Update"
              description="Quickly update product details like name, price, and description with our intuitive form."
            />
            <FeatureCard
              icon={<Trash2 className="text-blue-600 w-6 h-6" />}
              title="Delete Securely"
              description="Remove products from your list with a confirmation step to prevent accidental deletions."
            />
          </div>
        </div>
      </div>
    </div>
  );
}
