import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { ShoppingBag, Home, Package } from 'lucide-react';

export default function Layout({ children }) {
  const location = useLocation();

  const navLinks = [
    { name: 'Home', path: '/', icon: Home },
    { name: 'Products', path: '/products', icon: Package },
  ];

  return (
    <div className="min-h-screen bg-white flex flex-col">
      <header className="bg-blue-600 text-white shadow-sm sticky top-0 z-40">
        <nav className="max-w-7xl mx-auto px-6">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <Link to="/" className="flex-shrink-0 flex items-center gap-2">
                <ShoppingBag className="h-8 w-8 text-purple-200" />
                <span className="text-xl font-bold text-purple-200">Productify</span>
              </Link>
            </div>
            <div className="hidden md:block">
              <div className="ml-10 flex items-baseline space-x-4">
                {navLinks.map((link) => {
                  const isActive = location.pathname === link.path;
                  return (
                    <Link
                      key={link.name}
                      to={link.path}
                      className={`flex items-center gap-2 px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                        isActive
                          ? 'bg-blue-700 text-white'
                          : 'text-blue-100 hover:bg-blue-500 hover:bg-opacity-75'
                      }`}
                    >
                      <link.icon className="w-4 h-4" />
                      {link.name}
                    </Link>
                  );
                })}
              </div>
            </div>
          </div>
        </nav>
      </header>
      <main className="flex-grow bg-white">
        {children}
      </main>
      <footer className="bg-gray-800 text-white mt-auto">
        <div className="max-w-7xl mx-auto py-4 px-6 text-center text-sm">
          <p>&copy; {new Date().getFullYear()} Productify. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}
