import React, { useState, useEffect } from 'react';
import Modal from './ui/Modal';
import Button from './ui/Button';
import Input from './ui/Input';
import { Save } from 'lucide-react';

const categories = [
  "Electronics", "Clothing", "Books", "Home & Garden", 
  "Sports", "Beauty", "Toys", "Automotive","Food" ,"Furniture","Grocery","Footwear","Other"
];

export default function ProductForm({ product, onSave, onCancel, isOpen }) {
  const [formData, setFormData] = useState({
    name: '',
    price: '',
    description: '',
    category: ''
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (product) {
      setFormData({
        name: product.name || '',
        price: product.price || '',
        description: product.description || '',
        category: product.category || ''
      });
    } else {
      setFormData({ name: '', price: '', description: '', category: '' });
    }
    setErrors({});
  }, [product, isOpen]);

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = 'Product name is required';
    if (!formData.price || formData.price <= 0) newErrors.price = 'Price must be greater than 0';
    if (!formData.category) newErrors.category = 'Category is required';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsSubmitting(true);
    try {
      await onSave({ id: product?._id, ...formData, price: parseFloat(formData.price) });
    } catch (error) {
      console.error('Error saving product:', error);
    }
    setIsSubmitting(false);
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) setErrors(prev => ({ ...prev, [field]: null }));
  };

  return (
    <Modal 
      isOpen={isOpen} 
      onClose={onCancel}
      title={product ? 'Edit Product' : 'Add New Product'}
    >
      <form onSubmit={handleSubmit} className="space-y-6 p-6 bg-gray-50 rounded-xl shadow-inner">

        {/* Product Name */}
        <div className="space-y-1">
          <label htmlFor="name" className="block text-sm font-semibold text-gray-700">
            Product Name *
          </label>
          <Input
            id="name"
            value={formData.name}
            onChange={(e) => handleInputChange('name', e.target.value)}
            placeholder="Enter product name"
            className={`h-12 rounded-lg pl-3 shadow-sm border ${errors.name ? 'border-red-500' : 'border-gray-300'} focus:ring-2 focus:ring-blue-400 focus:border-blue-500`}
          />
          {errors.name && <p className="text-red-500 text-xs">{errors.name}</p>}
        </div>

        {/* Price */}
        <div className="space-y-1">
          <label htmlFor="price" className="block text-sm font-semibold text-gray-700">
            Price *
          </label>
          <Input
            id="price"
            type="number"
            step="0.01"
            min="0"
            value={formData.price}
            onChange={(e) => handleInputChange('price', e.target.value)}
            placeholder="0.00"
            className={`h-12 rounded-lg pl-3 shadow-sm border ${errors.price ? 'border-red-500' : 'border-gray-300'} focus:ring-2 focus:ring-blue-400 focus:border-blue-500`}
          />
          {errors.price && <p className="text-red-500 text-xs">{errors.price}</p>}
        </div>

        {/* Category */}
        <div className="space-y-1">
          <label htmlFor="category" className="block text-sm font-semibold text-gray-700">
            Category *
          </label>
          <select
            id="category"
            value={formData.category}
            onChange={(e) => handleInputChange('category', e.target.value)}
            className={`h-12 w-full rounded-lg pl-3 shadow-sm border ${errors.category ? 'border-red-500' : 'border-gray-300'} focus:ring-2 focus:ring-blue-400 focus:border-blue-500`}
          >
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
          {errors.category && <p className="text-red-500 text-xs">{errors.category}</p>}
        </div>

        {/* Description */}
        <div className="space-y-1">
          <label htmlFor="description" className="block text-sm font-semibold text-gray-700">
            Description
          </label>
          <textarea
            id="description"
            value={formData.description}
            onChange={(e) => handleInputChange('description', e.target.value)}
            placeholder="Enter product description (optional)"
            className="h-24 w-full rounded-lg pl-3 pt-2 shadow-sm border border-gray-300 placeholder-gray-400 focus:ring-2 focus:ring-blue-400 focus:border-blue-500 resize-none"
          />
        </div>

        {/* Buttons */}
        <div className="flex gap-4 pt-4">
          <Button
            type="button"
            variant="outline"
            onClick={onCancel}
            className="flex-1 border border-gray-300 text-gray-700 hover:bg-gray-100 rounded-lg transition-all duration-200"
            disabled={isSubmitting}
          >
            Cancel
          </Button>
          <Button
            type="submit"
            className="flex-1 bg-gradient-to-r from-blue-600 to-blue-500 hover:from-blue-700 hover:to-blue-600 text-white font-semibold rounded-lg shadow-lg transition-all duration-200"
            disabled={isSubmitting}
          >
            <Save className="w-5 h-5 mr-2 inline-block" />
            {isSubmitting ? 'Saving...' : (product ? 'Update' : 'Save')}
          </Button>
        </div>
      </form>
    </Modal>
  );
}
