import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { ShoppingBag, Home, Package } from 'lucide-react';

export default function Layout({ children }) {
  const location = useLocation();

  const navLinks = [
    { name: 'Home', path: '/', icon: Home },
    { name: 'Products', path: '/products', icon: Package },
  ];

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {/* Navbar */}
      <header className="bg-white shadow sticky top-0 z-50">
        <nav className="max-w-7xl mx-auto px-6 lg:px-8">
          <div className="flex justify-between items-center h-20">
            {/* Logo */}
            <Link to="/" className="flex items-center gap-3">
              <ShoppingBag className="h-10 w-10 text-blue-600" />
              <span className="text-2xl font-bold text-gray-800">Productify</span>
            </Link>

            {/* Navigation Links */}
            <div className="hidden md:flex items-center gap-4">
              {navLinks.map((link) => {
                const isActive = location.pathname === link.path;
                return (
                  <Link
                    key={link.name}
                    to={link.path}
                    className={`flex items-center gap-2 px-4 py-2 rounded-lg font-medium transition-all duration-200
                      ${
                        isActive
                          ? 'bg-blue-600 text-white shadow-md'
                          : 'text-gray-700 hover:bg-blue-50 hover:text-blue-600'
                      }`}
                  >
                    <link.icon className={`w-5 h-5 ${isActive ? 'text-white' : 'text-blue-500'}`} />
                    {link.name}
                  </Link>
                );
              })}
            </div>

            {/* Mobile Menu Placeholder (optional) */}
            {/* You can add hamburger menu here for mobile later */}
          </div>
        </nav>
      </header>

      {/* Main Content */}
      <main className="flex-grow">
        {children}
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-gray-300 mt-auto">
        <div className="max-w-7xl mx-auto py-4 px-6 text-center text-sm">
          <p>&copy; {new Date().getFullYear()} Productify. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}
